package com.socialmedia.exception;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.context.request.WebRequest;import org.springframework.web.servlet.View;import java.time.LocalDateTime;@ControllerAdvicepublic class GlobalExceptions {    private final View error;    public GlobalExceptions(View error) {        this.error = error;    }    @ExceptionHandler(Exception.class)    public ResponseEntity<ErrorDetatils> otherException(Exception ue, WebRequest req){        ErrorDetatils errorDetatils = new ErrorDetatils(ue.getMessage(),req.getDescription(false), LocalDateTime.now());        return new ResponseEntity<ErrorDetatils>((ErrorDetatils) error,HttpStatus.BAD_REQUEST);    }}